  render() {
    return (
      <form
        className='w-100'>
        <textarea 
        required
        maxLength='140' />
        <div className='info'>
          140
        </div>
        <button type='submit' className='bt btn-dark w-100'>
          Envoyer !
        </button>
      </form>
    )
  }
}
